-- =============================================
-- Author:		Daniel Floca
-- Create date: 2018-10-11
-- Description:	Stored procedure to add new cards
-- =============================================
CREATE PROCEDURE spAddCard 
	@DeckId INT,
	@Question VARCHAR(MAX),
	@Answer VARCHAR(MAX)
AS

BEGIN
SET NOCOUNT ON;
INSERT INTO dbo.Decks ([Name], UserId, DateCreated) VALUES (@Name, @UserId, GETDATE()) END

BEGIN
	SET NOCOUNT ON;

	UPDATE dbo.Cards
	SET IsDeleted = 1, DateDeleted = GETDATE()
	WHERE Id = @CardId;

END

BEGIN

	SET NOCOUNT ON;

    UPDATE dbo.Decks
	SET IsDeleted = 1, DateDeleted = GETDATE()
	WHERE Id = @DeckId;
END

BEGIN
SET NOCOUNT ON;
UPDATE dbo.Cards SET DeckId = @DeckId, Question = @Question, Answer = @Answer WHERE Id = @CardId END

-- =============================================
-- Author:		Daniel Floca
-- Create date:	2018-10-26
-- Description:	Stored procedure to update cards
-- =============================================
CREATE PROCEDURE [dbo].[spUpdateCard] 
	@CardId INT,
	@DeckId INT,
	@Question VARCHAR(MAX),
	@Answer VARCHAR(MAX)
AS

BEGIN

	SET NOCOUNT ON;

    UPDATE dbo.Decks
	SET Name = @Name, UserId = @UserId
	WHERE Id = @DeckId;
END

